import React, { Component } from 'react';

import MyComponent from "./MyComponent";
import SimpleReactLightbox from "simple-react-lightbox";

const axios = require('axios').default;
const moviesId = require('../moviesId.json');

export class Movie extends Component{
     enlarge(title, image, rating, director, plot){
          document.body.style.overflow = 'hidden';

          var lightBox = document.createElement('div');
          lightBox.id = 'lightBox';
          lightBox.className = 'lightBox_'
     }

     getMovieId(){
          let moviesIdList = []
          for( let movieId of moviesId){
               moviesIdList.push(movieId);
          }

          return moviesIdList
     }

     render(){
          let moviesIdList = this.getMovieId();

          return(
               moviesIdList.map((each) =>(
                    <div>
                         <Movie movie={each} enlarge={this.enlarge}/>
                    </div>
                    )
               )
          );
     }
}

export default Movie;
